    private void submitButtonActionPerformed(ActionEvent evt) throws URISyntaxException, SQLException {//GEN-FIRST:event_submitButtonActionPerformed
        // TODO add your handling code here:
        
        String username = usernameTextField.getText();
        String password = String.valueOf(enterPasswordTextField.getPassword());
        String conPassword = String.valueOf(reenterPasswordTextField.getPassword());
        
        if(username.equals("") || password.equals("") || conPassword.equals("")){
            JOptionPane.showMessageDialog(rootPane, "All the Fields are Mandatory");
        } else {
            if(!password.equals(conPassword)){
                JOptionPane.showMessageDialog(rootPane, "Passwords Do Not Match");
            } else {
                boolean flag = DatabaseUtility.forgotPassword(username, password);
                if(flag) {
                    JOptionPane.showMessageDialog(rootPane, "Password Updated Successfully");
                } else {
                    JOptionPane.showMessageDialog(rootPane, "Invalid Username");
                }
                
            }
        }
        
        
    }//GEN-LAST:event_submitButtonActionPerformed

